import React,{Component} from 'react';
import {Button,Icon,Grid,Segment,Card} from 'semantic-ui-react';
import Router from '../routes'

class AddButton extends Component{

	render(){
		if(this.props.admin){
			return(


						<Card>
							<Image />
							<Card.Content>
								<Card.Header> Add Show Name </Card.Header>
								<Card.Meta> {`Add-Date \nFrom : Add-From-Time To : Add-To-Time`}</Card.Meta>
							</Card.Content>
							<Card.Content extra>
								<a>
									Add show
								</a>
							</Card.Content>
						</Card>
			)
		}
		else{
			return null;
		}
	}
}

export default AddButton;
