import React,{Component} from 'react';
import Web3 from 'web3';
import TicketBooking from '../../../build/contracts/TicketBooking.json'
import {Icon,Button,Container} from 'semantic-ui-react'
import Layout from '../../components/layout'
import GetSeat from '../../components/getSeat'

const getContract = async()=>{

	const {web3,networkId} = await getWeb3();
	let contract;

	const netData = TicketBooking.networks[networkId]

	if(netData){
		contract = await new web3.eth.Contract(TicketBooking.abi,netData.address);
	}
	else{
		window.alert("please change your network selection");
		}
	return contract
}

const getWeb3 = async()=>{

	let web3,networkId;

	if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined'){

		web3 = new Web3(window.ethereum);
		const connect = await window.ethereum.enable();
		networkId = await window.ethereum.networkVersion
		console.log("111");
	}

	else if(typeof window !== 'undefined' && typeof window.web3 !== 'undefined'){

		web3 = new Web3(window.web3.currentProvider);
		networkId = await web3.eth.net.getId();
		console.log("2222")
	}

	else{

		const provider = new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/0468426dce2148b480c61bc7c52be067");
		web3 = new Web3(provider);
		networkId = await web3.eth.net.getId();
		console.log("3333")
	}
	return{web3,networkId};
}

const getBookedSeat = async(id)=>{

	const contract = await getContract()
	const name = await contract.methods.name().call()
	const showCount = await contract.methods.getShowCount().call();
	let show;
	let seats=[];
	console.log(showCount)
	if(id<showCount && id>=0){
		show = await contract.methods.showDetail(id).call()
		seats = await contract.methods.getBookedSeats(id).call()
	}
	return {seats,show,name}
}

class ViewShow extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : false,
			admin : false,
			loading : true
		}
	}

	static async getInitialProps(query){
		const id = query.query.id
		console.log("static")
		const {seats,name,show} = await getBookedSeat(id)
		return {id,seats,show,name}
	}

	async componentDidMount(){
		console.log('components')
		await this.getAccount()
		this.setState({loading : false})
	}

	getAccount = async ()=>{
		let address;
		if(typeof window && typeof window.ethereum){
			await window.ethereum.enable();
			address = await ethereum.selectedAddress
			if(address){
				this.setState({account : true})
			}
			console.log("acc 1",address)
		}

		else if(typeof window.web3.eth ){
			window.web3.eth.getAccounts((err,acc)=>{
				if(err){
					console.log(err);
				}
				else{
					if(acc.length >= 1)
					this.setState({account : true})
					console.log(this.state.account,acc)
					address = acc;
					console.log("acc 2",acc)
				}
			})	
		}
		if(address){
			const {web3} = await getWeb3();
			if(this.props.admin === web3.utils.toChecksumAddress(address))
				this.setState({admin : true})
		}
		return {address}
	}

	buyTicket = async(index,seatNo,amount)=>{
		if(this.state.account){
			const contract = await getContract();
			const {address} = await this.getAccount()
			this.setState({loading : true})
			await contract.methods.bookSeat(index,seatNo).send({from : address,value : amount});
			this.setState({loading : true})
			window.alert("Seat Booked")
			Router.pushRoute("view_show")
		}
		else{
			window.alert("No Account Found")
		}
	}


	render(){

		if(this.state.true){
			return(
				<Layout 
					name={this.props.name}
					account={this.state.account}
					id={'Logged in'}
				>

				<GetSeat show={this.props.show}
					seats={this.props.seats}
					buyTicket={this.buyTicket}
					showId={this.props.id}
					amount={this.props.show.amount}
				/>
				</Layout>
			)
		}
		else{
			<Container>
				<Head>
					<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
				</Head>
				<Dimmer active>
					<Loader size='massive'>Loading</Loader>
				</Dimmer>
			</Container>
		}
	}
}

export default ViewShow;
