import React,{Component} from 'react';
import Layout from '../components/layout';
import web3 from '../components/web3'

class App extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : false,
			networkId : 0
		}
	}

	async getContract(){
		if(typeof web3.eth.net !== 'undefined'){
			const netId = await web3.eth.net.getId();
			contract = await new web3.eth.Contract(TicketBooking.abi,TicketBooking.networks[netId].address);
			return contract
			console.log("first",showCount)
		}
		else{
			console.log("second")
			await web3.version.getNetwork((err,id)=>{
					if(err){
						console.log(err)
					}
					else{
						networkId = id
					}
				})
			const netData = TicketBooking.networks[networkId]
			console.log('netdata',netData)
			if(netData){
				contract = await new web3.eth.Contract(TicketBooking.abi,netData.address);
			}
			else{
				window.alert("the contract is not deployed in this network");
			} 

		}

	}


	getAccount = async ()=>{
		web3.eth.getAccounts((err,acc)=>{
			if(err){
				console.log(err);
			}
			else{
				if(acc.length >= 1)
				this.setState({account : true})
				console.log(this.state.account,acc)
			}
		})

	}

	static async getInitialProps(){
		//const name = ticketBooking
		console.log('name')
		return {}
	}

	async componentDidMount(){
		this.getAccount();
		console.log("init")
	}

	render(){

		return (
			<div>
				<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
				<Layout account={this.state.account}/>
			</div>
			)
	}
}

export default App;
