import React,{Component} from 'react';
import {Button} from 'semantic-ui-react';

class AddButton extends Component{
	render(){
		if(this.props.admin){
			return(
				<Button circular icon='add'/>
			)
		}
		else{
			return null;
		}
	}
}

export default AddButton;
