import React,{Component} from 'react';
import {Button,Icon,Grid} from 'semantic-ui-react'

class GetSeat extends Component{

	ticket = ()=>{
		var res=[]
		for(let i=1;i<=this.props.show.seatsAvailable;i++){
			let Color = this.props.seats.includes(i)?'red':'grey'
	 		res.push(
					<Button size='tiny' circular key={i} color={Color}>
					<Icon name='ticket'/>
					{i}
					</Button>
			)
		}
		return res;
	}

	render(){
		return(
		<Grid.Row>
			<Grid.Column>
				{this.ticket()}
			</Grid.Column>
		</Grid.Row>
		)
	}
}

export default GetSeat;
