import React,{Component} from 'react';
import Layout from '../components/layout';
import {web3Fun} from '../components/getWeb3';
import {contractFun} from '../components/getContract'
import {getShowDetails} from '../components/getShowDetails';
import ShowDetail from '../components/showDetail';
import {Dimmer,Card,Loader,Container,Grid} from 'semantic-ui-react';
import Head from 'next/head';

class App extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : false,
			admin : false,
			loading : true
		}
	}

	changeAdminStatus = (adminState)=>{
		this.setState({ admin : adminState})
	}

	getAccount = async ()=>{
		let address;
		if(typeof window!='undefined' && typeof window.ethereum!='undefined'){
			await window.ethereum.enable();
			address = await ethereum.selectedAddress
			if(address){
				this.setState({account : true})
			}
			console.log("acc 1",address)
		}

		else if(typeof window!='undefined' && typeof window.web3!='undefined' && typeof window.web3.eth!='undefined' ){
			window.web3.eth.getAccounts((err,acc)=>{
				if(err){
					console.log(err);
				}
				else{
					if(acc.length >= 1)
					this.setState({account : true})
					console.log(this.state.account,acc)
					address = acc;
					console.log("acc 1")
				}
			})	
		}
		if(address){
			const {web3} = await web3Fun();
			if(this.props.admin === web3.utils.toChecksumAddress(address))
				this.setState({admin : true})
		}
	}

	static async getInitialProps(){
		const {contract} = await contractFun()
		const name = await contract.methods.name().call()
		const admin = await contract.methods.admin().call()
		const {shows} = await getShowDetails();
		console.log('index static')
		return {name,shows,admin}
	}

	async componentDidMount(){
		await this.getAccount();
		console.log("init")
		this.setState({loading : false})
	}

	render(){

		if(!this.state.loading){
			return (
				<Layout account={this.state.account}
						name={this.props.name}
						id={'Logged in'}
						>
					<Grid.Row>
						<Grid.Column>
								<Card.Group>
									<ShowDetail shows={this.props.shows}
										admin={this.state.admin}
									/>
								</Card.Group>
						</Grid.Column>
					</Grid.Row>
				</Layout>
				)
		}
		else{
			return(
				<Container>
					<Head>
						<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
					</Head>
					<Dimmer active>
						<Loader size='massive'>Loading</Loader>
					</Dimmer>
				</Container>
			)
		}
	}
}

export default App;
