	render(){
		return(

			<Menu borderless inverted>
				<Link route="home">
					<Menu.Item
						name="Ticket Booking"
					/>
				</Link>
				<Menu.Item position='right' active
					name = {this.state.account ? 'Logged in' : 'Not Logged in'}
					color = {this.state.account ? 'green' : 'red'}
				/>
			</Menu>
			)
	}
}

export default Layout;
