import React from 'react';
import TicketBooking from '../../build/contracts/TicketBooking.json'
import {web3} from './web3'

let networkId,ticketBooking;

const getContract = async ()=>{
	if(typeof web3.eth.net !== 'undefined'){
		const netId = await web3.eth.net.getId();
		//console.log(netId,TicketBooking.abi,TicketBooking.networks[netId].address)
		ticketBooking = await new web3.eth.Contract(TicketBooking.abi,TicketBooking.networks[netId].address);
		console.log(ticketBooking.methods)
	}
	else{
		await web3.version.getNetwork((err,id)=>{
				if(err){
					console.log(err)
				}
				else{
					networkId = id
				}
			})
		const netData = TicketBooking.networks[networkId]
		console.log('netdata',netData)
		if(netData){
			ticketBooking = await new web3.eth.Contract(TicketBooking.abi,netData.address);
		}
		else{
			window.alert("the contract is not deployed in this network");
		} 

	}

}

getContract()

export {ticketBooking}
