import React,{Component} from 'react';
import {Menu} from 'semantic-ui-react';
import {Link} from '../routes';
//import web3 from './web3';

class Layout extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : ''
		}
	}

	async getAccount(){
		let web3;
		if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined'){

			web3 = new Web3(window.ethereum);
			const connect = await window.ethereum.enable();
			console.log("111");
		}

		else if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined'){

			web3 = new Web3(window.web3.currentProvider);
			console.log("2222")
		}

		else{

			const provider = new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/0468426dce2148b480c61bc7c52be067");
			web3 = new Web3(provider);
			console.log("3333")
		}
		await web3.eth.getAccounts((err,acc)=>{
			if(err){
				console.log(err);
			}
			else{
				this.setState({account : acc[0]})
			}
		})

	}

	render(){
		return(

			<Menu borderless inverted>
				<Link route="home">

						<Menu.Item
							name="Ticket Booking"
							onClick = {this.getAccount}
						/>
				</Link>
				<Menu.Item 
					name={this.account}
					/>
			</Menu>
			)
	}
}

export default Layout;
