import React,{Component} from 'react';
import ViewSeat from './viewSeat'
import {Button,Icon} from 'semantic-ui-react'

class GetSeat extends Component{

	render(){
		for(let i=1;i<=this.props.seat.seatsAvailable;i++){
			return(
				<Button color='red'>
					<Icon name='ticket'/>
					{this.props.seat}
				</Button>
			)
		}
	}
}

export default GetSeat;
