import React,{Component} from 'react';
import ViewSeat from './viewSeat'
import {Button,Icon} from 'semantic-ui-react'

class GetSeat extends Component{

	ticket = ()=>{
		var res=[]
		for(let i=1;i<=this.props.show.seatsAvailable;i++){
			let Color = this.props.show.includes(i)?'red':'grey'
	 		res.push(
					<Button key={i} color={Color}>
					<Icon name='ticket'/>
					{i}
					</Button>
			)
		}
		return res;
	}

	render(){
		return(
		<div>
		{this.ticket()}
		</div>
		)
	}
}

export default GetSeat;
