import React,{Component} from 'react';
import Layout from '../../components/layout';
import {web3Fun} from '../../components/getWeb3';
import {contractFun} from '../../components/contractFun';
import AddShow from '../../components/admin-components/addShow'
import {Grid,Container,Loader,Dimmer} from 'semantic-ui-react';
import Head from 'next/head'

class AdminAddShow extends Component{

	constructor(props){
		super(props);
		this.state = {
			admin : false,
			loading : true
		}
	}
	static async getInitialProps(){
		const contract = await contractFun();
		const name = await contract.methods.name().call()
		const admin = await contract.methods.admin().call()
		return{name,admin}
	}

	async componentDidMount(){
		await this.getAccount();
		this.setState({loading : false})
	}

	getAccount = async ()=>{
		let address;
		if(typeof window && typeof window.ethereum){
			await window.ethereum.enable();
			address = await ethereum.selectedAddress
		}

		else if(typeof window.web3.eth ){
			window.web3.eth.getAccounts((err,acc)=>{
				if(err){
					console.log(err);
				}
				else{
					address = acc;
				}
			})	
		}
		if(address){
			const {web3} = await web3Fun();
			if(this.props.admin === web3.utils.toChecksumAddress(address))
				this.setState({admin : true})
		}
	}

	changeLoading = (load)=>{
		this.setState({loading : load})
		console.log("got it ....")
	}

	render(){

		if(!this.state.loading){

			return(
				<Layout 
					id={'Admin'}
					account={this.state.admin}
					name={this.props.name}
					>
					<Grid.Row><Grid.Column>
						<AddShow admin={this.state.admin}
							adminAcc={this.props.admin}
							changeLoading={this.changeLoading}
						/>
					</Grid.Column> </Grid.Row>
				</Layout>
			)
		}
		else{

			return(
				<Container>
					<Head>
						<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
					</Head>
					<Dimmer active>
						<Loader size='massive'>Loading</Loader>
					</Dimmer>
				</Container>
			)
		}
	}
}

export default AdminAddShow;
