import React,{Component} from 'react';
import Layout from '../components/layout';
import Web3 from 'web3';
import ShowDetail from '../components/showDetail';
import TicketBooking from '../../build/contracts/TicketBooking.json'
import {Button} from 'semantic-ui-react';

const getContract = async()=>{

	const web3 = await getWeb3();
	let contract;

	if(typeof web3.eth !== 'undefined'){

		const netId = await web3.eth.net.getId();
		contract = await new web3.eth.Contract(TicketBooking.abi,TicketBooking.networks[netId].address);
		console.log("first")
	}
	else{

		console.log("second")
		await web3.version.getNetwork((err,id)=>{
				if(err){
					console.log(err)
				}
				else{
					networkId = id
				}
			})

		const netData = TicketBooking.networks[networkId]
		console.log('netdata',netData)

		if(netData){
			contract = await new web3.eth.Contract(TicketBooking.abi,netData.address);
		}
		else{
			window.alert("the contract is not deployed in this network");
		} 
	}
	return contract
}

const getWeb3 = async()=>{

	let web3;

	if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined'){

		web3 = new Web3(window.ethereum);
		const connect = await window.ethereum.enable();
		console.log("111");
	}

	else if(typeof window !== 'undefined' && typeof window.web3 !== 'undefined'){

		web3 = new Web3(window.web3.currentProvider);
		console.log("2222")
	}

	else{

		const provider = new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/0468426dce2148b480c61bc7c52be067");
		web3 = new Web3(provider);
		console.log("3333")
	}
	return(web3);
}

class App extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : false,
			networkId : 0
		}
	}

	getAccount = async ()=>{
		web3.eth.getAccounts((err,acc)=>{
			if(err){
				console.log(err);
			}
			else{
				if(acc.length >= 1)
				this.setState({account : true})
				console.log(this.state.account,acc)
			}
		})

	}

	static async getInitialProps(){
		const contract = await getContract();
		const name = await contract.methods.name().call()
		const showCount = await contract.methods.getShowCount().call()
		return {name}
	}

	async componentDidMount(){
		this.getAccount();
		console.log("init")
	}

	addShow = async()=>{
		const contract = await getContract()
		const web3 = await getWeb3()
		const account = await window.ethereum.selectedAccount 
		const amount = web3.eth.toWei('0.003','ether');
		await contract.methods.addShow(true,"Petta",101,amount,"4.00pm","6.00pm","12/11/2020")
			.send({from : account})
	}

	render(){

		return (
			<div>
				<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
				<Layout account={this.state.account}
					name={this.props.name}
				/>
				<ShowDetail />
				<Button onClick={this.addShow}>add</Button>
			</div>
			)
	}
}

export default App;
