import React,{Component} from 'react';
import {Button,Icon,Grid,Confirm} from 'semantic-ui-react'

class GetSeat extends Component{

	constructor(props){
		super(props);
		this.state = {
			open : false,
			id : null
		}
	}

	openConfirm = (Color,id)=>{

		if(Color=='grey'){
			//this.setState({open : true})
			console.log('grey')
		}
		else{
			window.alert("Seat have been booked already !")
		}
	}

	ticket = ()=>{
		var res=[]
		for(let i=1;i<=this.props.show.seatsAvailable;i++){
			let Color = this.props.seats.includes(i)?'red':'grey'
	 		res.push(
					<Button size='tiny' onClick={this.openConfirm(Color,i)} circular key={i} color={Color}>
					<Icon name='ticket'/>
					{i}
					</Button>
			)
		}
		return res;
	}

	render(){
		return(
		<Grid.Row>
			<Grid.Column>
				{this.ticket()}
				<Confirm open={this.state.open}
				 onCancel={()=>this.setState({open:false})}
				 onConfirm={()=>this.props.buyTicket(this.props.showId,this.state.id)}
				/>
			</Grid.Column>
		</Grid.Row>
		)
	}
}

export default GetSeat;
