import React,{Component} from 'react';
import {Button,Icon} from 'semantic-ui-react';
import Router from '../routes'

class AddButton extends Component{

	render(){
		if(this.props.admin){
			return(
				<Button animated floated='right' onClick={()=>Router.pushRoute('add_show')}>
					<Button.Content visible>Add Show</Button.Content>
					<Button.Content hidden>
						<Icon name='add'/>
					</Button.Content>
				</Button>
			)
		}
		else{
			return null;
		}
	}
}

export default AddButton;
