import React,{Component} from 'react';
import {Menu,Label} from 'semantic-ui-react';
import {Link} from '../routes';
import web3 from './web3';

class Layout extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : false
		}
	}

	getAccount = async()=>{
		await web3.eth.getAccounts((err,acc)=>{
			if(err){
				console.log(err);
			}
			else{
				if(acc.length >= 1)
				this.setState({account : true})
				console.log(this.state.account)
			}
		})

	}

	render(){
		return(

			<Menu borderless inverted>
				<Link route="home">
					<Menu.Item
						name="Ticket Booking"
						onClick = {this.getAccount}
					/>
				</Link>
				<Label color={this.state.account ? 'teal' : 'red'} position ='right'>
					{this.state.account ? 'Logged in' : 'Not Logged in'}
				</Label> 
			</Menu>
			)
	}
}

export default Layout;
