import React,{Component} from 'react';
import {Form,Button,Header,Radio,Modal,Image,Icon,Input} from 'semantic-ui-react';
import Web3 from 'web3';
import TicketBooking from '../../../build/contracts/TicketBooking.json'

const getContract = async()=>{

	const {web3,networkId} = await getWeb3();
	let contract;

	const netData = TicketBooking.networks[networkId]

	if(netData){
		contract = await new web3.eth.Contract(TicketBooking.abi,netData.address);
	}
	else{
		window.alert("please change your network selection");
		}
	return contract
}

const getWeb3 = async()=>{

	let web3,networkId;

	if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined'){

		web3 = new Web3(window.ethereum);
		const connect = await window.ethereum.enable();
		networkId = await window.ethereum.networkVersion
		console.log("111");
	}

	else if(typeof window !== 'undefined' && typeof window.web3 !== 'undefined'){

		web3 = new Web3(window.web3.currentProvider);
		networkId = await web3.eth.net.getId();
		console.log("2222")
	}

	else{

		const provider = new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/0468426dce2148b480c61bc7c52be067");
		web3 = new Web3(provider);
		networkId = await web3.eth.net.getId();
		console.log("3333")
	}
	return{web3,networkId};
}

class AddShow extends Component{

	constructor(props){
		super(props);
		this.state = {
			showAvailable : true,
			seatsAvailable : 0,
			showName : '',
			amount : 0,
			fromTime : '',
			toTime : '',
			date : '',
			open : false
		}
	}

	close = ()=>{

		this.setState({open : false})
	}

	onSubmit = async(event)=>{
		event.preventDefault();
		const {web3} = await getWeb3();
		const {showAvailable,seatsAvailable,showName,amount,fromTime,toTime,date} = this.state;
		const contract = await getContract();
		const amountWei = await web3.utils.toWei(amount,ether);
		adminAddr = await web3.utils.toChecksumAddress(this.props.adminAcc);
		await contract.methods.addShow(showAvailable,showName,seatsAvailable,amountWei,fromTime,toTime,date).send({from : adminAddr})
		window.alert("Transaction Completed");
	}


	render(){

		if(this.props.admin){
			return(
				<div>
					<Form>
						<Form.Group unstackable widths={2}>
							<Form.Input label='Show Name' placeholder='Show Name'
								value={this.state.showName}
							 	onChange={(event)=>this.setState({showName : event.target.value})}
							 />
							<Form.Input type='number' label='Available Seats' placeholder='Available Seats' min='0' max='10000'
								value={this.state.seatsAvailable}
							 	onChange={(event)=>this.setState({seatsAvailable : event.target.value})}
							/>
						</Form.Group>
						<Form.Group unstackable widths={2}>
							<Form.Input label='Amount' placeholder='Amount'
								value={this.state.amount}
							 	onChange={(event)=>this.setState({amount : event.target.value})}
							/>
							<Form.Input type='date' label='Date'
								value={this.state.date}
							 	onChange={(event)=>this.setState({date : event.target.value})}
							 />
						</Form.Group>
						<Form.Group unstackable widths={2}>
							<Form.Input type='time' label='From Time' 
								value={this.state.fromTime}
							 	onChange={(event)=>this.setState({fromTime : event.target.value})}
							/>
							<Form.Input type='time' label='To Time' 
								value={this.state.toTime}
							 	onChange={(event)=>{
							 		this.setState({toTime : event.target.value})
							 		console.log(event.target.value)
							 	}}
							/>
						</Form.Group>
						<Form.Checkbox label='Whether Show is Available for Booking' 
							value={this.state.amount}
						 	onChange={(event)=>this.setState({amount : event.target.value})}
						/>
						<Modal trigger={<Button onClick={()=>this.setState({open : true})}>Submit</Button>} centered={false} open={this.state.open} onClose={this.close}>
						<Modal.Header>Are you sure to Add this show ?</Modal.Header>
						<Modal.Content image>
							<Image wrapped size='medium' src=''/>
							<Modal.Description>
								<Header>Name : {this.state.showName}</Header>
								<p>Seats Available : {this.state.seatsAvailable}</p>
								<p>Amount : {this.state.amount}</p>
								<p>From : {this.state.fromTime}</p>
								<p>To : {this.state.toTime}</p>
								<p>Date : {this.state.date}</p>
							</Modal.Description>
						</Modal.Content>
						<Modal.Actions>
							<Button color='red' onClick={this.close}><Icon name='remove'/>No</Button>
							<Button color='green' onClick={this.onSubmit}><Icon name='checkmark'/>Yes</Button>
						</Modal.Actions>
						</Modal>	
					</Form>
					</div>
			)
		}
		else{
			return(
				<h1>You are not allowed to access this page</h1>
			)
		}
	}
}

export default AddShow;
