import React from 'react';
import {web3,networkId} from './getWeb3';
import TicketBooking from '../../build/contracts/TicketBooking.json'

let contract;

const contractFun = async()=>{
	const netData = await TicketBooking.networks[networkId]
	console.log('NetData :\n',netData)

	if(netData){
		contract = await new web3.eth.Contract(TicketBooking.abi,netData.address);
	}
	else if(typeof window!='undefined'){
		window.alert("please change your network selection");
	}
}
contractFun();

export {contract};
