import React,{Component} from 'react';
import Layout from '../../components/layout'
import getContract from '../../components/getContract'
import getAccount from '../../components/getAccount'
import {Head,Container,Loader,Dimmer} from 'semantic-ui-react'

class UpdateShow extends Component{

	constructor(props){
		super(props);
		this.state = {
			admin : false,
			loading : true
		}
	}

	static async getInitialProps(){
		const {contract} = await getContract();
		const name = contract.methods.name().call();
		const admin = contract.methods.admin().call();
		return {name,admin}
	}

	async componentDidMount(){
		const {isAdmin} = await getAccount(this.props.admin)
		this.setState({
			admin : isAdmin,
			loading : false
		})
	}

	render(){
		if(this.state.loading){
			return(
				<Layout
					id={'Admin'}
					account={this.state.admin}
					name={this.props.name}
				>
					HII
				</Layout>
			)
		}
		else{
			return(
				<Container>
					<Head>
						<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
					</Head>
					<Dimmer>
						<Loader size='massive'>Loading</Loader>
					</Dimmer>
				</Container>
			)
		}
	}
}

export default UpdateShow
