import React,{Component} from 'react';
import {Form,Button,Radio} from 'semantic-ui-react';

class AddShow extends Component{

	constructor(props){
		super(props);
		this.state = {
			showAvailable : true,
			showName : '',
			amount : 0,
			fromTime : '',
			toTime : '',
			date : ''
		}
	}

	render(){

		if(this.props.admin){
			return(
				<Form>
					<Form.Group unstackable widths={2}>
						<Form.Input label='Show Name' placeholder='Show Name'/>
						<Form.Input type='number' label='Available Seats' placeholder='Available Seats'/>
					</Form.Group>
					<Form.Group unstackable widths={2}>
						<Form.Input label='Amount' placeholder ='Amount'/>
						<Form.Input type='date' label='Date' placeholder='Date'/>
					</Form.Group>
					<Form.Group>
						<Form.Input type='time' label='From Time' placeholder='From Time'/>
						<Form.Input type='time' label='To Time' placeholder='To Time'/>
					</Form.Group>
					<Radio slider checked={this.state.showAvailable}/>
					<Button submit>Submit</Button>
				</Form>
			)
		}
		else{
			return(
				<h1>You are not allowed to access this page</h1>
			)
		}
	}
}

export default AddShow;
