import React,{Component} from 'react';
import {Form,Button,Header,Radio,Modal,Image,Icon,Input} from 'semantic-ui-react';

class AddShow extends Component{

	constructor(props){
		super(props);
		this.state = {
			showAvailable : true,
			seatsAvailable : 0,
			showName : '',
			amount : 0,
			fromTime : '',
			toTime : '',
			date : ''
		}
	}


	render(){

		if(this.props.admin){
			return(
				<div>
					<Form>
						<Form.Group unstackable widths={2}>
							<Form.Input label='Show Name' placeholder='Show Name'
								value={this.state.showName}
							 	onChange={(event)=>this.setState({showName : event.target.value})}
							 />
							<Form.Input type='number' label='Available Seats' placeholder='Available Seats' min='0' max='10000'
								value={this.state.seatsAvailable}
							 	onChange={(event)=>this.setState({seatsAvailable : event.target.value})}
							/>
						</Form.Group>
						<Form.Group unstackable widths={2}>
							<Form.Input label='Amount' placeholder='Amount'
								value={this.state.amount}
							 	onChange={(event)=>this.setState({amount : event.target.value})}
							/>
							<Form.Input type='date' label='Date'
								value={this.state.date}
							 	onChange={(event)=>this.setState({date : event.target.value})}
							 />
						</Form.Group>
						<Form.Group unstackable widths={2}>
							<Form.Input type='time' label='From Time' 
								value={this.state.fromTime}
							 	onChange={(event)=>this.setState({fromTime : event.target.value})}
							/>
							<Form.Input type='time' label='To Time' 
								value={this.state.toTime}
							 	onChange={(event)=>{
							 		this.setState({toTime : event.target.value})
							 	}}
							/>
						</Form.Group>
						<Form.Checkbox label='Whether Show is Available for Booking' 
							value={this.state.amount}
						 	onChange={(event)=>this.setState({amount : event.target.value})}
						/>
						<Modal trigger={<Button >Submit</Button>} centered={false}>
						<Modal.Header>Are you sure to Add this show ?</Modal.Header>
						<Modal.Content image>
							<Image wrapped size='medium' src=''/>
							<Modal.Description>
								<Header>{this.state.showName}</Header>
								<p>{this.state.seatsAvailable}</p>
								<p>{this.state.amount}</p>
								<p>{this.state.fromTime}</p>
								<p>{this.state.toTime}</p>
								<p>{this.state.date}</p>
							</Modal.Description>
						</Modal.Content>
						<Modal.Actions>
							<Button color='red'><Icon name='remove'/>No</Button>
							<Button color='green'><Icon name='checkmark'/>Yes</Button>
						</Modal.Actions>
						</Modal>	
					</Form>
					</div>
			)
		}
		else{
			return(
				<h1>You are not allowed to access this page</h1>
			)
		}
	}
}

export default AddShow;
