import React,{Component} from 'react';
import {Button,Icon,Grid} from 'semantic-ui-react';
import Router from '../routes'

class AddButton extends Component{

	render(){
		if(this.props.admin){
			return(
				<Grid.Row>
					<Button animated='fade' floated='right' onClick={()=>Router.pushRoute('add_show')}>
						<Button.Content visible>Add Show</Button.Content>
						<Button.Content hidden>
							<Icon name='add'/>
						</Button.Content>
					</Button>
				</Grid.Row>
			)
		}
		else{
			return null;
		}
	}
}

export default AddButton;
