import React,{Component} from 'react';
import Layout from '../components/layout';
import Web3 from 'web3';
import ShowDetail from '../components/showDetail';
import TicketBooking from '../../build/contracts/TicketBooking.json'
import {Button} from 'semantic-ui-react';

const getContract = async()=>{

	const {web3,networkId} = await getWeb3();
	let contract;

	const netData = TicketBooking.networks[networkId]

	if(netData){
		contract = await new web3.eth.Contract(TicketBooking.abi,netData.address);
	}
	else{
		window.alert("please change your network selection");
		}
	return contract
}

const getWeb3 = async()=>{

	let web3,networkId;

	if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined'){

		web3 = new Web3(window.ethereum);
		const connect = await window.ethereum.enable();
		networkId = await window.ethereum.networkVersion
		console.log("111");
	}

	else if(typeof window !== 'undefined' && typeof window.web3 !== 'undefined'){

		web3 = new Web3(window.web3.currentProvider);
		networkId = await web3.eth.net.getId();
		console.log("2222")
	}

	else{

		const provider = new Web3.providers.HttpProvider("https://rinkeby.infura.io/v3/0468426dce2148b480c61bc7c52be067");
		web3 = new Web3(provider);
		networkId = await web3.eth.net.getId();
		console.log("3333")
	}
	return{web3,networkId};
}

const getShowDetails = async()=>{
	const contract = await getContract();
	const showCount = await contract.methods.getShowCount().call()
	let shows=[];
	let {web3} = await getWeb3();
	if(typeof web3.utils){
		web3 = await web3.utils
		console.log('came')
	}
	for(var i=0;i<showCount;i++){
		const show = await contract.methods.showDetail(i).call()
		shows.push({
			showAvailable : show.showAvailable,
			showName : show.showName,
			seatsAvailable : show.seatsAvailable,
			amount : web3.fromWei(show.amount,'ether'),
			fromTime : show.fromTime,
			toTime : show.toTime,
			date : show.date
		})
	}
	return {shows};

}

class App extends Component{

	constructor(props){
		super(props);
		this.state = {
			account : false,
			admin : false
		}
	}

	getAccount = async ()=>{
		let address;
		if(typeof window && typeof window.ethereum){
			await window.ethereum.enable();
			address = await ethereum.selectedAddress
			if(address){
				this.setState({account : true})
			}
			console.log("acc 1",address)
		}

		else if(typeof window.web3.eth ){
			window.web3.eth.getAccounts((err,acc)=>{
				if(err){
					console.log(err);
				}
				else{
					if(acc.length >= 1)
					this.setState({account : true})
					console.log(this.state.account,acc)
					address = acc;
					console.log("acc 1")
				}
			})	
		}
		console.log(this.props.admin,'admin',address,address === this.props.admin)
		if(address){
			const {web3} = await getWeb3();
			if(address === web3.utils.toChecksumAddress(this.props.admin))
				this.setState({admin : true})
		}
	}

	static async getInitialProps(){
		const contract = await getContract();
		const name = await contract.methods.name().call()
		const admin = await contract.methods.admin().call()
		const {shows} = await getShowDetails();
		console.log(shows)
		return {name,shows,admin}
	}

	async componentDidMount(){
		await this.getAccount();
		this.addButton();
		console.log("init")
	}

	 addShow = async()=>{
		const contract = await getContract()
		const {web3} = await getWeb3()
		const account = await window.ethereum.selectedAddress
		console.log("Selecetd Account",account)
		const amount = web3.utils.toWei('0.005','ether');
		await contract.methods.addShow(false,"master",200,amount,"6.00am","8.00am","22/01/2021").send({from : account})
	} 
	addButton = ()=>{
		console.log('add Button',this.state.admin)
		if(this.state.admin){
			return(
				<Button circular icon='add'/>
			)
		}
	}

	render(){

		return (
			<div>
				<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
				<Layout account={this.state.account}
					name={this.props.name}
				/>
				<ShowDetail shows={this.props.shows}/>
			</div>
			)
	}
}

export default App;
