import React from 'react';
import {Menu} from 'semantic-ui-react';
import {Link} from '../routes';
import web3 from './web3';

const getAccount = async()=>{
	const acc = await web3.eth.getAccounts();
	console.log(acc);
}

export default ()=>{

	return(

		<Menu borderless inverted>
			<Link route="home">
				<a>
					<Menu.Item
						name="Ticket Booking"
					/>
				</a>
			</Link>
		</Menu>
		)
}
