import React,{Component} from 'react';
import {Form,Button,Image,Modal,Icon} from 'semantic-ui-react'
import {getWeb3} from '../getWeb3'

class EditShow extends Component{


	constructor(props){
		super(props);
		this.state = {
			showName : '',
			availableSeats : 0,
			amount : 0,
			fromTime : '',
			toTime : '',
			date : '',
			image : '',
			fileBuffer : undefined,
			file : undefined,
			open : false
		}
	}
	fileInputRef = React.createRef()

	async componentDidMount(){
		const show = this.props.show
		const {web3} = await getWeb3()
		this.setState(
			{
				showName : show.showName,
				availableSeats : show.seatsAvailable,
				amount : web3.utils.fromWei(show.amount,'ether'),
				date : show.date,
				fromTime : show.fromTime,
				toTime : show.toTime,
				image : show.image
			}
		)
		const date = new Date()
		console.log("date ",date)
	}

	close=()=>{
		this.setState({open : false})
	}

	render(){
		if(this.props.admin){
			return(
				<Form>
					<Form.Group unstackable widths={2}>
						<Form.Input label="Show Name" placeholder="Show Name"
							value={this.state.showName}
							onChange={(e)=>{this.setState({showName : e.target.value})}}
						/>
						<Form.Input label="Available Seats" placeholder="Available Seats"
							value={this.state.availableSeats}
						/>
					</Form.Group>
					<Form.Group unstackable widths={2}>
						<Form.Input label="Amount" placeholder="Amount"
							value={this.state.amount}
						/>
						<Form.Input label="date" type="date" defaultValue={this.state.date}/>
					</Form.Group>
					<Form.Group unstackable widths={2}>
						<Form.Input label="From Time" type="time" defaultValue={this.state.fromTime}/>
						<Form.Input label="To Time" type="time" defaultValue={this.state.toTime}/>
					</Form.Group>
					<Form.Group unstackable widths={2}>
						<Image src={`https://gateway.ipfs.io/ipfs/${this.state.image}`} size="medium" rounded />
					</Form.Group>
					<Form.Group unstackable widths={2}>
						<Form.Field>
							<Button
								content="Choose File"
								labelPosition="left"
								icon="file"
								onClick={()=>this.fileInputRef.current.click()}
							/>
							<input
								ref={this.fileInputRef}
								type="file"
								hidden
								onChange={(e)=>{
									const reader = new window.FileReader()
									const file = e.target.files[0]
									reader.readAsArrayBuffer(file)
									reader.onloadend = async()=>this.setState({
										fileBuffer : await Buffer.from(reader,result),
										file : URL.createObjectURL(file)
									})
								}}
							/>
						</Form.Field>
					</Form.Group>
					<Modal trigger={<Button onClick={()=>this.setState({open: true})}>Update</Button>} open={this.state.open} onClose={this.close}>
						<Modal.Header>Are you sure to Update this show ?</Modal.Header>
						<Modal.Content image>
						</Modal.Content>
						<Image wrapped size="medium" src={this.state.file}/>
						<Modal.Description>

						</Modal.Description>
						<Modal.Content>
							<Modal.Actions>
							<Button color='red' onClick={this.close}><Icon name='remove'/>No</Button>
							<Button color='green' onClick={this.onSubmit}><Icon name='checkmark'/>Yes</Button>
							</Modal.Actions>
						</Modal.Content>
					</Modal>
				</Form>
			)
		}
		else{
			return <h1>You are not allowed to access this page</h1>
		}
	}
}

export default EditShow;
