import React from 'react';
import TicketBooking from '../../build/contracts/TicketBooking.json'
import web3 from './web3'

let networkId,contract;

const getContract = async ()=>{
	await web3.version.getNetwork((err,id)=>{
			if(err){
				console.log(err)
			}
			else{
				networkId = id
			}
		})
	const netData = TicketBooking.networks[networkId]
	if(netData){
		contract = await await new web3.eth.Contract(TicketBooking.abi,netData.address);
	}
}

export {contract}
