import React,{Component} from 'react';
import {Card,Image,Icon} from 'semantic-ui-react';

class showDetails extends Component{

	constructor(props){
		super(props);
	}

	render(){
		return(
			<Card.Group>
				{this.props.shows.map((show,index)=>{
					return(
						<Card key={index}>
							<Image />
							<Card.Content>
								<Card.Header> {show.showName} </Card.Header>
								<Card.Meta> {`${show.date} \nFrom : ${show.fromTime} To : ${show.toTime}`}</Card.Meta>
							</Card.Content>
							<Card.Content extra>
								<a>
									<Icon name='ethereum'/>
									{show.amount} ether
								</a>
							</Card.Content>
						</Card>
					)
					})}
				<AddButton admin={this.state.admin}/>
			</Card.Group>
		)	
	}
}

export default showDetails;
